import React, { useState, useEffect } from "react";

export default function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState("Aucun r√©sultat de d√©tection...");
  const [nodes, setNodes] = useState([]);
  const [annotatedImage, setAnnotatedImage] = useState(null);

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch("http://192.168.137.1:8000/nodes");
        const data = await res.json();
        setNodes(data || []);
      } catch (err) {
        console.error("Erreur fetch nodes :", err);
      }
    }, 1000);
    return () => clearInterval(interval);
  }, []);

 const getColor = (status) => {
  const s = Number(status);
  if (s === 1) return "#4ade80"; // vert
  if (s === 0) return "#f87171"; // rouge
  return "#60a5fa"; // bleu (inconnu / non joignable)
};


  const handleUpload = async (e) => {
  const file = e.target.files[0];
  if (!file) return;

  setImage(URL.createObjectURL(file));
  setAnnotatedImage(null); // on efface l‚Äôancienne image annot√©e

  const formData = new FormData();
  formData.append("image", file);

  try {
    const res = await fetch("http://192.168.137.1:8000/relay", {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    setResult(data.message || "‚úÖ Image re√ßue par master");

    // üü£ ICI ‚Üí on va chercher le premier r√©sultat disponible
    // On essaie plusieurs fois, car YOLO prend un peu de temps
    let tries = 0;
    const maxTries = 10;

    const checkResult = async () => {
      try {
        const r = await fetch("http://192.168.137.1:8000/last_result");
        const json = await r.json();

        if (json.annotated_image) {
          // Peu importe quel FogNode a r√©pondu : on prend le premier r√©sultat disponible
          setAnnotatedImage("data:image/jpeg;base64," + json.annotated_image);
        } else if (tries < maxTries) {
          tries++;
          setTimeout(checkResult, 300); // on r√©essaie apr√®s 300ms
        } else {
          console.log("Pas de r√©sultat annot√© re√ßu √† temps");
        }
      } catch (err) {
        console.error("Erreur fetch last_result", err);
      }
    };

    setTimeout(checkResult, 500); // on laisse un petit temps pour que le node finisse YOLO

  } catch (err) {
    console.error(err);
    setResult("‚ùå Master ne r√©pond pas");
  }
};

  return (
    <div style={styles.container}>


      <div style={styles.right}>
        <div style={styles.header}>Nodes Status</div>
        {["FogNode1","FogNode2","FogNode3"].map((name) => {
          const node = nodes.find(n => n.name === name);
          return <div key={name} style={{...styles.nodeStyle, backgroundColor: getColor(node?.available)}}>{name}</div>
        })}
      </div>
    </div>
  );
}

const styles = {
  container: { display: "flex", width: "100vw", height: "100vh", justifyContent: "center", alignItems: "center", background: "linear-gradient(135deg,#fbc2eb,#fde047)" },
  left: { display: "flex", flexDirection: "column", marginRight: "200px", gap: "20px", alignItems: "center" },
  right: { display: "flex", flexDirection: "column", alignItems: "center", gap: "15px" },
  header: { width: "220px", padding:"12px", textAlign:"center", fontWeight:"bold", fontSize:"20px", borderRadius:"10px", background:"#9f23d1", color:"white" },
  imageBox: { width:"420px", height:"320px", borderRadius:"15px", background:"#d8b4fe", display:"flex", justifyContent:"center", alignItems:"center", border:"3px dashed white", overflow:"hidden" },
  image: { width:"100%", height:"100%", objectFit:"cover", borderRadius:"15px" },
  resultBox: { width:"420px", padding:"15px", borderRadius:"10px", background:"#c084fc", textAlign:"center", fontSize:"18px", fontWeight:"bold", color:"white" },
  nodeStyle: { width:"200px", padding:"12px", borderRadius:"10px", fontWeight:"bold", color:"white", textAlign:"center", fontSize:"18px" }
};
