import React, { useState, useEffect } from "react";

export default function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState("Aucun résultat de détection...");
  const [nodes, setNodes] = useState([]);

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch("http://192.168.137.1:8000/nodes");
        const data = await res.json();
        setNodes(data || []);
      } catch (err) {
        console.error("Erreur lors du fetch nodes :", err);
      }
    }, 500);

    return () => clearInterval(interval);
  }, []);

  const getColor = (status) => {
    if (status === 1) return "#4ade80";
    if (status === 0) return "#f87171";
    return "#60a5fa";
  };

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setImage(URL.createObjectURL(file));

    const formData = new FormData();
    formData.append("image", file);

    try {
      const masterRes = await fetch("http://192.168.137.1:8000/relay", {
        method: "POST",
        body: formData,
      });

      const masterData = await masterRes.json();
      setResult(masterData.message || "✅ Image reçue par master");
    } catch (err) {
      console.error("Erreur upload :", err);
      setResult("❌ Master ne répond pas");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.left}>
        <div style={styles.imageBox}>
          {image ? (
            <img src={image} alt="fog detect" style={styles.image} />
          ) : (
            <p>Charge une image</p>
          )}
        </div>

        <div style={styles.resultBox}>{result}</div>

        <input type="file" onChange={handleUpload} />
      </div>

      <div style={styles.right}>
        <div style={styles.header}>Nodes Status</div>

        {["FogNode1", "FogNode2", "FogNode3"].map((name) => {
          const node = nodes.find((n) => n.name === name);
          return (
            <div
              key={name}
              style={{ ...styles.nodeStyle, backgroundColor: getColor(node?.available) }}
            >
              {name}
            </div>
          );
        })}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    width: "100vw",
    height: "100vh",
    justifyContent: "center",
    alignItems: "center",
    background: "linear-gradient(135deg, #fbc2eb, #fde047)",
  },
  left: {
    display: "flex",
    flexDirection: "column",
    marginRight: "200px",
    gap: "20px",
    alignItems: "center",
  },
  right: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "15px",
  },
  header: {
    width: "220px",
    padding: "12px",
    textAlign: "center",
    fontWeight: "bold",
    fontSize: "20px",
    borderRadius: "10px",
    background: "#9f23d1",
    color: "white",
  },
  imageBox: {
    width: "420px",
    height: "320px",
    borderRadius: "15px",
    background: "#d8b4fe",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    border: "3px dashed white",
    overflow: "hidden",
  },
  image: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    borderRadius: "15px",
  },
  resultBox: {
    width: "420px",
    padding: "15px",
    borderRadius: "10px",
    background: "#c084fc",
    textAlign: "center",
    fontSize: "18px",
    fontWeight: "bold",
    color: "white",
  },
  nodeStyle: {
    width: "200px",
    padding: "12px",
    borderRadius: "10px",
    fontWeight: "bold",
    color: "white",
    textAlign: "center",
    fontSize: "18px",
  },
};
