import React, { useState, useEffect } from "react";

export default function App() {
  const [image, setImage] = useState(null);
  const [result, setResult] = useState("Aucun rÃ©sultat de dÃ©tection...");
  const [nodes, setNodes] = useState([]);
  const [annotatedImage, setAnnotatedImage] = useState(null);
  const [fullResult, setFullResult] = useState(null);

  useEffect(() => {
    const interval = setInterval(async () => {
      try {
        const res = await fetch("http://192.168.137.1:8000/nodes");
        const data = await res.json();
        setNodes(data || []);
      } catch (err) {
        console.error("Erreur fetch nodes :", err);
      }
    }, 50);
    return () => clearInterval(interval);
  }, []);

  const getColor = (status, isConnected) => {
    if (!isConnected) return "#60a5fa";     // bleu = non connectÃ©
    if (status === 1) return "#4ade80";     // vert = dispo
    if (status === 0) return "#f87171";     // rouge = occupÃ©
    return "#60a5fa";                       // fallback
  };














  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // ðŸ”¥ Tout effacer
    setImage(null);
    setAnnotatedImage(null);
    setFullResult(null);

    const formData = new FormData();
    formData.append("image", file);

    try {
      const res = await fetch("http://192.168.137.1:8000/relay", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setResult(data.message);

      if (res.status === 503) {
        return;
      }

      // Attente YOLO
      let tries = 0;
      const maxTries = 10;

      const checkResult = async () => {
        try {
          const r = await fetch("http://192.168.137.1:8000/last_result");
          const json = await r.json();

          if (json.annotated_image) {
            setAnnotatedImage("data:image/jpeg;base64," + json.annotated_image);
            setFullResult(json);
          } else if (tries < maxTries) {
            tries++;
            setTimeout(checkResult, 300);
          }
        } catch (err) {
          console.error("Erreur fetch last_result", err);
        }
      };

      setTimeout(checkResult, 500);

    } catch (err) {
      console.error(err);
      setResult("âŒ Master ne rÃ©pond pas");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.left}>

        {/* ðŸ”µ Message dâ€™attente (AFFICHÃ‰ SEULEMENT avant YOLO) */}
        {!annotatedImage && (
          <div style={styles.imageBox}>
            <p style={{ fontSize: "18px", color: "white" }}>
              En attente du rÃ©sultat du Fog...
            </p>
          </div>
        )}

        {/* ðŸŸ¢ Image annotÃ©e */}
        {annotatedImage && (
          <div style={styles.imageBox}>
            <img src={annotatedImage} style={styles.image} alt="annotated result" />
          </div>
        )}

        {/* RÃ©sultats */}
        <div style={styles.resultBox}>
          {fullResult ? (
            <div style={{ textAlign: "left" }}>
              <p><b>Node :</b> {fullResult.node}</p>
              <p><b>DÃ©tections :</b></p>

              {fullResult.detections.length === 0 ? (
                <p>Aucune dÃ©tection</p>
              ) : (
                fullResult.detections.map((det, idx) => (
                  <div key={idx}>
                    â€¢ {det.class} ({(det.confidence * 100).toFixed(1)}%)
                  </div>
                ))
              )}
            </div>
          ) : (
            result
          )}
        </div>

        <input type="file" onChange={handleUpload} />

      </div>

      <div style={styles.right}>
        <div style={styles.header}>Nodes Status</div>

        {["FogNode1", "FogNode2", "FogNode3"].map((name) => {
          const node = nodes.find((n) => n.name === name);
          const isConnected = !!node;
          const available = node?.available ?? null;

          return (
            <div
              key={name}
              style={{
                ...styles.nodeStyle,
                backgroundColor: getColor(available, isConnected),
              }}
            >
              {name}
            </div>
          );
        })}
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: "flex",
    width: "100vw",
    height: "100vh",
    justifyContent: "center",
    alignItems: "center",
    background: "linear-gradient(135deg,#fbc2eb,#fde047)",
  },
  left: {
    display: "flex",
    flexDirection: "column",
    marginRight: "200px",
    gap: "20px",
    alignItems: "center",
  },
  right: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "15px",
  },
  header: {
    width: "220px",
    padding: "12px",
    textAlign: "center",
    fontWeight: "bold",
    fontSize: "20px",
    borderRadius: "10px",
    background: "#9f23d1",
    color: "white",
  },
  imageBox: {
    width: "420px",
    height: "320px",
    borderRadius: "15px",
    background: "#d8b4fe",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    border: "3px dashed white",
    overflow: "hidden",
  },
  image: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    borderRadius: "15px",
  },
  resultBox: {
    width: "420px",
    padding: "15px",
    borderRadius: "10px",
    background: "#c084fc",
    textAlign: "center",
    fontSize: "18px",
    fontWeight: "bold",
    color: "white",
  },
  nodeStyle: {
    width: "200px",
    padding: "12px",
    borderRadius: "10px",
    fontWeight: "bold",
    color: "white",
    textAlign: "center",
    fontSize: "18px",
  },
};
